DROP TABLE new_students;

CREATE TABLE new_students (
	new_student_id		serial primary key,
	acct_no				varchar(50),
	last_name			varchar(50),
	first_name			varchar(50),
	middle_name			varchar(50),
	major_mame			varchar(50),		
	degree_code			varchar(50)
);


UPDATE new_students SET degree_code = upper(trim(degree_code));

UPDATE new_students SET degree_code = 'ANAT' WHERE degree_code = 'ANAI';
UPDATE new_students SET degree_code = 'INSY' WHERE degree_code = 'CS';
UPDATE new_students SET degree_code = 'COMT' WHERE degree_code = 'CT';
UPDATE new_students SET degree_code = 'INLD' WHERE degree_code = 'ILD';
UPDATE new_students SET degree_code = 'NRSG' WHERE degree_code = 'NSRG';
UPDATE new_students SET degree_code = 'MCRB' WHERE degree_code = 'MBIO';


