DROP TABLE new_students;
CREATE TABLE new_students (
	MATRIC_NO			varchar(50),
	SURNAME				varchar(50),
	FIRST_NAME			varchar(50),
	MIDDLE_NAME			varchar(50),
	MODULE				varchar(50),
	BURSARY_ACCT_NO		varchar(50),
	majorid				varchar(50),
	DEGREE_IN_VIEW		varchar(50)
);



	studentid, surname, firstname, othernames, 
	accountnumber, departmentid)

SELECT 'CHRI', 2, 'M', 'S', 'NG', 'NG', 'NG', 39, 
	upper(trim(new_students.MATRIC_NO)), new_students.SURNAME, new_students.FIRST_NAME, new_students.MIDDLE_NAME, 
	new_students.BURSARY_ACCT_NO, majors.departmentid

FROM new_students INNER JOIN majors ON trim(upper(new_students.majorid)) = trim(upper(majors.majorid))
LEFT JOIN students ON upper(trim(new_students.MATRIC_NO)) = students.studentid

WHERE students.studentid is null;


UPDATE new_students SET DEGREE_IN_VIEW = 'PHD' WHERE DEGREE_IN_VIEW = 'PhD';
UPDATE new_students SET DEGREE_IN_VIEW = 'PHD' WHERE DEGREE_IN_VIEW = 'MPM';
UPDATE new_students SET DEGREE_IN_VIEW = 'PHD' WHERE DEGREE_IN_VIEW = 'Mphil';
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'M.Sc';
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'M.A';
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = '';
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW is null;
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'M.sc';
UPDATE new_students SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'M.A.';
UPDATE new_students SET DEGREE_IN_VIEW = 'PGDI' WHERE DEGREE_IN_VIEW = 'PGD';


SELECT new_students.DEGREE_IN_VIEW
FROM new_students LEFT JOIN sublevels ON new_students.DEGREE_IN_VIEW = sublevels.sublevelid
WHERE sublevels.sublevelid is null
GROUP BY new_students.DEGREE_IN_VIEW;

SELECT 'MA', new_students.DEGREE_IN_VIEW, 3, 2, new_students.MATRIC_NO
FROM new_students LEFT JOIN studentdegrees ON (new_students.MATRIC_NO = studentdegrees.studentid) 
WHERE (studentdegrees.studentid is null);

SELECT studentdegrees.studentdegreeid, new_students.majorid, 2
FROM (new_students INNER JOIN studentdegrees ON studentdegrees.studentid = new_students.MATRIC_NO)
	LEFT JOIN  studentmajors ON studentdegrees.studentdegreeid = studentmajors.studentdegreeid
WHERE (studentmajors.studentdegreeid is null);







