DROP TABLE new_students2;
CREATE TABLE new_students2 (
	MATRIC_NO			varchar(50),
	SURNAME				varchar(50),
	FIRST_NAME			varchar(50),
	MIDDLE_NAME			varchar(50),
	MODULE				varchar(50),
	BURSARY_ACCT_NO		varchar(50),
	majorid				varchar(50),
	DEGREE_IN_VIEW		varchar(50)
);


	studentid, surname, firstname, othernames, 
	accountnumber, departmentid)

SELECT 'CHRI', 2, 'M', 'S', 'NG', 'NG', 'NG', 39, 
	upper(trim(new_students2.MATRIC_NO)), new_students2.SURNAME, new_students2.FIRST_NAME, new_students2.MIDDLE_NAME, 
	new_students2.BURSARY_ACCT_NO, majors.departmentid

FROM new_students2 INNER JOIN majors ON trim(upper(new_students2.majorid)) = trim(upper(majors.majorid))
LEFT JOIN students ON upper(trim(new_students2.MATRIC_NO)) = upper(trim(students.studentid))

WHERE students.studentid is null;

UPDATE new_students2 SET DEGREE_IN_VIEW = 'PHD' WHERE DEGREE_IN_VIEW = 'Mphil';
UPDATE new_students2 SET DEGREE_IN_VIEW = 'PHD' WHERE DEGREE_IN_VIEW = 'PhD';
UPDATE new_students2 SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'MPM';
UPDATE new_students2 SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW = 'M.Sc.';
UPDATE new_students2 SET DEGREE_IN_VIEW = 'MAST' WHERE DEGREE_IN_VIEW is null;
UPDATE new_students2 SET DEGREE_IN_VIEW = 'PGDI' WHERE DEGREE_IN_VIEW = 'PGD';

SELECT 'MA', new_students2.DEGREE_IN_VIEW, 3, 2, new_students2.MATRIC_NO
FROM new_students2 LEFT JOIN studentdegrees ON (new_students2.MATRIC_NO = studentdegrees.studentid) 
WHERE (studentdegrees.studentid is null);

SELECT studentdegrees.studentdegreeid, new_students2.majorid, 2
FROM (new_students2 INNER JOIN studentdegrees ON studentdegrees.studentid = new_students2.MATRIC_NO)
	LEFT JOIN  studentmajors ON studentdegrees.studentdegreeid = studentmajors.studentdegreeid
WHERE (studentmajors.studentdegreeid is null);




