CREATE OR REPLACE FUNCTION insstudentname() RETURNS trigger AS $$
DECLARE
	mycnt RECORD;
	myemail VARCHAR(120);
BEGIN	
	NEW.studentname := UPPER(NEW.surname)	|| ', ' || UPPER(NEW.firstname) || ' ' || UPPER(COALESCE(NEW.othernames, ''));

	IF (NEW.emailuser is null) THEN
		myemail := lower(NEW.surname) || substring(lower(NEW.firstname) from 1 for 2);

		SELECT INTO mycnt count(studentid) as emailcount
		FROM students
		WHERE (emailuser = myemail);
	
		IF (mycnt.emailcount = 0) THEN
			NEW.emailuser := myemail;
		ELSE
			NEW.emailuser := myemail || mycnt.emailcount;
		END IF;
	END IF;

	RETURN NEW;
END;
$$ LANGUAGE plpgsql VOLATILE;

CREATE TRIGGER insstudentname
  BEFORE INSERT OR UPDATE
  ON students
  FOR EACH ROW
  EXECUTE PROCEDURE insstudentname();



