
CREATE TABLE pg_majors (
	pg_major_id 				serial primary key,	
	majorid						varchar(16),
	majorname					varchar(320),
	major_title					varchar(320),
	Degree_to_be_awarded		varchar(320)
);


UPDATE pg_majors SET degree_to_be_awarded = trim(upper(degree_to_be_awarded));

UPDATE pg_majors SET majorid = trim(upper(majorid));

CREATE TABLE pg_courses (
	courseid			varchar(50),
	degreelevelid		varchar(50),
	coursetitle			varchar(160),
	credithours			integer
);



UPDATE pg_courses SET courseid = trim(upper(courseid)), degreelevelid = trim(upper(degreelevelid));


CREATE TABLE pg_students (
	pg_student		serial primary key,
	degreeid		varchar(50),
	degreename		varchar(50),
	majorid			varchar(50),
	majorname		varchar(50),
	studentid		varchar(50),
	surname			varchar(50),
	firstname		varchar(50),
	othernames		varchar(50),
	sex				varchar(50)
);


UPDATE pg_students SET degreeid = trim(upper(degreeid)), majorid = trim(upper(majorid));

CREATE TABLE pg_results (
	pg_result_id			serial primary key,
	studentid				varchar(50),
	studentname				varchar(160),
	quarterid				varchar(50),
	courseid				varchar(50),
	coursetitle				varchar(160),
	finalmarksid			real,
	grade					varchar(50)
);


UPDATE pg_results SET studentid = upper(trim(studentid)), quarterid = upper(trim(quarterid)), courseid = upper(trim(courseid)), grade = upper(trim(grade));

