CREATE TABLE imp_licenses (
	license_id			integer primary key,
	license_type_id		integer,
	currency_unit_id 		integer,
	license_name		varchar(120) not null unique,
	license_period		integer default 1 not null,
	application_fee		real default 0 not null,
	initial_fee		real default 0 not null,
	annual_fee		real default 0 not null,
	agt_fee			real default 0 not null,
	type_approval_fee		real default 0 not null,
	annual_feedetail 	varchar (240),
	license_abbrev		varchar (120),
	num  			varchar (20),				
	application_account	varchar(32),
	initial_account		varchar(32),
	annual_account		varchar(32),
	ta_account		varchar(32),
	fixed_fee		char(1) default '0' not null,
	rollout_period		integer default 0 not null,
	Quarterly		char(1) default '0' not null,
	Annually		char(1) default '0' not null,
	isactive		char(1) default '1' not null,
	license_report		varchar(120),

	is_active   char(1) default '0',
	is_terrestrial 		char(1) default '0',		--terrestrial need 1k on top of initial payments
	is_vhf			char(1) default '0',
	is_maritime		char(1) default '0',
	is_broadcasting char(1) default '0',

	grace_period_years	integer,

	is_squential		char(1) default '0',		--whether or not the license number changes
	next_sequence_val		integer,

	spectrum_access		clob,
	license_terms		clob,
	details			clob
	);
  CREATE SEQUENCE imp_licenses_id_seq MINVALUE 1 INCREMENT BY 1 START WITH 1;
CREATE OR REPLACE TRIGGER tr_imp_license_id BEFORE INSERT ON imp_licenses
for each row 
begin     
	if inserting then 
		if :NEW.license_id is null then
			SELECT imp_licenses_id_seq.nextval into :NEW.license_id from dual;
		end if;
	end if; 
end;
/


